'use strict'

class Controller {
    init(app, query) {
        this.app = app;
        this.query = query;
        return this;
    }

    run(action) {
        let actionName = 'action' + action.charAt(0).toUpperCase() + action.slice(1);

        if (typeof this[actionName] !== 'function')
            throw new Error('It is not a function');

        this[actionName]();

        return this;
    }
}

module.exports = Controller;
