'use strict'

const pug = require('pug');

class View {
    constructor(app) {
        // Public properties
        // Private properties
        this._app = app;
        this._path = `${app.baseDir}/views`;
        this._extinsion = 'pug';
    }

    generate(view, data) {
        this._app.body = pug.renderFile(`${this._path}/${view}.${this._extinsion}`, data);
        this._app.send();
    }
}

module.exports = View;
